pragma solidity >=0.5.0 <0.7.0;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";

contract UxioERC20 is ERC20{
    constructor(string memory name, string memory symbol, uint8 decimals, address account, uint256 amount) ERC20(name, symbol) public {
        require(account != address(0), "Tokens cannot be minted to the zero address");
        _setupDecimals(decimals);
        _mint(account, amount);
    }
}
